"188 :   private static String extractClassNameIfGoog(Node node, Node parent,
189 :       String functionName){
190 :     String className = null;
191 :     if (NodeUtil.isExprCall(parent)) {
192 :       Node callee = node.getFirstChild();
193 :       if (callee != null && callee.getType() == Token.GETPROP) {
194 :         String qualifiedName = callee.getQualifiedName();
195 :         if (functionName.equals(qualifiedName)) {
196 :           Node target = callee.getNext();
197 :           if (target != null) {
198 :             className = target.getString();
199 :           }
200 :         }
201 :       }
202 :     }
203 :     return className;
204 :   }"

