"36 :     @Override
37 :     public int translate(CharSequence input, int index, Writer out) throws IOException {
38 :         int seqEnd = input.length();
39 :         // Uses -2 to ensure there is something after the &#
40 :         if(input.charAt(index) == '&' && index < seqEnd - 1 && input.charAt(index + 1) == '#') {
41 :             int start = index + 2;
42 :             boolean isHex = false;
43 : 
44 :             char firstChar = input.charAt(start);
45 :             if(firstChar == 'x' || firstChar == 'X') {
46 :                 start++;
47 :                 isHex = true;
48 : 
49 :                 // Check there's more than just an x after the &#
50 :             }
51 : 
52 :             int end = start;
53 :             // Note that this supports character codes without a ; on the end
54 :             while(input.charAt(end) != ';') 
55 :             {
56 :                 end++;
57 :             }
58 : 
59 :             int entityValue;
60 :             try {
61 :                 if(isHex) {
62 :                     entityValue = Integer.parseInt(input.subSequence(start, end).toString(), 16);
63 :                 } else {
64 :                     entityValue = Integer.parseInt(input.subSequence(start, end).toString(), 10);
65 :                 }
66 :             } catch(NumberFormatException nfe) {
67 :             System.err.println("FAIL: " + input.subSequence(start, end) + "[" + start +"]["+ end +"]");
68 :                 return 0;
69 :             }
70 : 
71 :             if(entityValue > 0xFFFF) {
72 :                 char[] chrs = Character.toChars(entityValue);
73 :                 out.write(chrs[0]);
74 :                 out.write(chrs[1]);
75 :             } else {
76 :                 out.write(entityValue);
77 :             }
78 : 
79 : 
80 :             return 2 + (end - start) + (isHex ? 1 : 0) + 1;
81 :         }
82 :         return 0;
83 :     }"

