"605 :     public static String abbreviate(String str, int lower, int upper, String appendToEnd) {
606 :         // initial parameter checks
607 :         if (str == null) {
608 :             return null;
609 :         }
610 :         if (str.length() == 0) {
611 :             return StringUtils.EMPTY;
612 :         }
613 : 
614 :         // if the lower value is greater than the length of the string,
615 :         // set to the length of the string
616 :         // if the upper value is -1 (i.e. no limit) or is greater
617 :         // than the length of the string, set to the length of the string
618 :         if (upper == -1 || upper > str.length()) {
619 :             upper = str.length();
620 :         }
621 :         // if upper is less than lower, raise it to lower
622 :         if (upper < lower) {
623 :             upper = lower;
624 :         }
625 : 
626 :         StringBuffer result = new StringBuffer();
627 :         int index = StringUtils.indexOf(str, " ", lower);
628 :         if (index == -1) {
629 :             result.append(str.substring(0, upper));
630 :             // only if abbreviation has occured do we append the appendToEnd value
631 :             if (upper != str.length()) {
632 :                 result.append(StringUtils.defaultString(appendToEnd));
633 :             }
634 :         } else if (index > upper) {
635 :             result.append(str.substring(0, upper));
636 :             result.append(StringUtils.defaultString(appendToEnd));
637 :         } else {
638 :             result.append(str.substring(0, index));
639 :             result.append(StringUtils.defaultString(appendToEnd));
640 :         }
641 :         return result.toString();
642 :     }"

